import React from 'react';
import './App.css';
import Container from './Container';
import Navbar from './navbar';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      name : ''

    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleChange(e) {
    this.setState({ name : e.target.value})
  }

  //on submit this will send the fetch req about the pokemon to the API
  handleSubmit(e) {
    console.log(this.state.name)
    fetch(`https://pokeapi.co/api/v2/pokemon/${this.state.name}`)
    .then(res => res.json())
    .then(data => console.log(data))
    .catch((err) => console.log(err))
    e.preventDefault();
  }


  render() {
    return (
      <div className="App">
        <Container info={this.state.name}/>
        <Navbar />
        <br/>
        <form onSubmit={this.handleSubmit}>
        <label>
          <input type="text" value={this.state.value} onChange={this.handleChange} placeholder="Search here!" />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>
    );
  }
}

export default App;
